--
-- DATABASE SUPERLEAGUE
--

DROP TABLE IF EXISTS CLUB;
DROP TABLE IF EXISTS TORNEO;
DROP TABLE IF EXISTS GIOCATORI;
DROP TABLE IF EXISTS ALLENATORE;
DROP TABLE IF EXISTS STADIO;
DROP TABLE IF EXISTS PARTITA;
DROP TABLE IF EXISTS EMITTENTE_TV;
DROP TABLE IF EXISTS ARBITRO;
DROP TABLE IF EXISTS GIORNATA;
DROP TABLE IF EXISTS SPONSOR;
DROP TABLE IF EXISTS BIGLIETTO;

-- CREAZIONE TABELLE

CREATE TABLE CLUB (
	NOME 				varchar(50)		primary key,
	COLORE_MAGLIA		varchar(32)		not null,
	NAZIONALITA 		varchar(32)		not null,
	RUOLO				varchar(20)		not null,
	PRESIDENTE			varchar(32)		not null,
	ANNO_FONDAZIONE		smallint		not null,
	TROFEI				smallint		not null,
	CAPITALE			integer			not null,
	NOME_STADIO			varchar(50),
	NOME_SPONSOR		varchar(32),
	foreign key (NOME_STADIO) references STADIO(NOME)	on update cascade on delete cascade,
	foreign key (NOME_SPONSOR) references SPONSOR(NOME)	on update cascade on delete cascade
);

CREATE TABLE TORNEO (
	GIRONE				smallint		primary key,
	RISULTATO			varchar(10)		not null,
	ORARIO				time			not null,
	NOME_CLUB_CASA		varchar(50),
	NOME_CLUB_TRASFERTA	varchar(50),
	foreign key (NOME_CLUB_CASA) references CLUB(NOME)	on update cascade on delete cascade,
	foreign key (NOME_CLUB_TRASFERTA) references CLUB(NOME)	on update cascade on delete cascade
);

CREATE TABLE GIOCATORI (
	CF					varchar(20)		primary key,
	NOME				varchar(32)		not null,
	COGNOME				varchar(32)		not null,
	N_MAGLIA			smallint		not null,
	PIEDE				varchar(10)		not null,
	RUOLO				varchar(15)		not null,
	DATA_NASCITA		date			not null,
	NAZIONALITA			varchar(32)		not null,
	STIPENDIO			integer			not null,
	ANNI_CONTRATTO		smallint		not null,
	VALORE_MERCATO		integer			not null,
	PRESENZE			smallint		not null,
	NOME_SPONSOR		varchar(32),
	NOME_CLUB			varchar(50),
	foreign key (NOME_CLUB) references CLUB(NOME)	on update cascade on delete cascade,
	foreign key (NOME_SPONSOR) references SPONSOR(NOME)	on update cascade on delete cascade
);

CREATE TABLE ALLENATORE (
	CF					varchar(20)		primary key,
	NOME				varchar(32)		not null,
	COGNOME				varchar(32)		not null,
	DATA_NASCITA		date			not null,
	NAZIONALITA			varchar(32)		not null,
	STIPENDIO			integer			not null,
	ANNI_CONTRATTO		date			not null,
	MODULO				varchar(10)		not null,
	MEDIA_PTI			decimal(5,2)	not null,
	NOME_CLUB			varchar(50),
	foreign key (NOME_CLUB) references CLUB(NOME)	on update cascade on delete cascade
);

CREATE TABLE STADIO (
	NOME				varchar(32)		primary key,
	ANNO_COSTRUZIONE	smallint		not null,
	CAPIENZA			integer			not null,
	PROPRIETA			varchar(2)		not null,
	CITTA				varchar(20)		not null
);

CREATE TABLE PARTITA (
	COD					varchar(6)		primary key,
	ORA 				time			not null,
	NUMERO_GIORNATA		smallint,
	NOME_STADIO			varchar(32),
	NOME_CLUB_CASA		varchar(50),
	GOAL_CASA			smallint		not null,
	NOME_CLUB_TRASFERTA	varchar(50),
	GOAL_TRASFERTA		smallint		not null,
	CF_ARBITRO			varchar(10),
	NOME_TV				varchar(32),
	foreign key (NUMERO_GIORNATA) references GIORNATA(NUMERO) on update cascade on delete cascade,
	foreign key (NOME_STADIO) references STADIO(NOME) on update cascade on delete cascade,
	foreign key (NOME_CLUB_CASA) references CLUB(NOME)	on update cascade on delete cascade,
	foreign key (NOME_CLUB_TRASFERTA) references CLUB(NOME)	on update cascade on delete cascade,
	foreign key (CF_ARBITRO) references ARBITRO(CF)	on update cascade on delete cascade,
	foreign key (NOME_TV) references EMITTENTE_TV(NOME)	on update cascade on delete cascade
);

CREATE TABLE EMITTENTE_TV (
	NOME				varchar(32)		primary key,
	CRONISTA			varchar(50)		not null,
	OPINIONISTA			varchar(50)		not null,
	LINGUA				varchar(20)		not null
);

CREATE TABLE ARBITRO (
	CF					varchar(10)		primary key,
	NOME				varchar(32)		not null,
	COGNOME				varchar(32)		not null,
	ETA					date			not null,
	NAZIONALITA			varchar(32)		not null
);

CREATE TABLE GIORNATA (
	NUMERO				smallint		primary key,
	DATA_G				date			not null
	
);

CREATE TABLE SPONSOR (
	NOME				varchar(32)		primary key,
	NETTO				integer			not null,
	BONUS				integer			not null	
);

CREATE TABLE BIGLIETTO (
	COD					varchar(10)		primary key,
	TITOLARE			varchar(50)		not null,
	PREZZO				decimal(7,2)	not null,
	TIPO				varchar(20)		not null,
	DATA_INIZIO			date			not null,
	DATA_FINE			date			not null,
	SETTORE				smallint		not null,
	FILA				smallint		not null,
	POSTO				smallint,
	NOME_CLUB			varchar(50),
	COD_PARTITA			varchar(10),
	foreign key (NOME_CLUB) references CLUB(NOME)	on update cascade on delete cascade,
	foreign key (COD_PARTITA) references PARTITA(COD)	on update cascade on delete cascade
);

